import React from 'react';
import createReactClass from 'create-react-class';
import * as UU5 from "uu5g04";
import ns from "./bricks-ns.js";
import Link from './link';

import './factory.less';

export const Factory = {

  createTag: function (tag, uu5Name, className, defaultContent, levelFrom, levelTo) {
    return createReactClass({
      displayName: uu5Name || tag,
      //@@viewOn:mixins
      mixins: [UU5.Common.BaseMixin, UU5.Common.ElementaryMixin, UU5.Common.ContentMixin, UU5.Common.NestingLevelMixin, UU5.Common.PureRenderMixin],
      //@@viewOff:mixins

      //@@viewOn:statics
      statics: {
        tagName: ns.name(uu5Name || tag),
        nestingLevelList: UU5.Environment.getNestingLevelList(levelFrom, levelTo),
        classNames: {
          main: ns.css((uu5Name || tag).toLowerCase()) + (className ? ' ' + className : '')
        },
        defaults: {
          content: defaultContent || null
        }
      },
      //@@viewOff:statics

      //@@viewOn:propTypes
      //@@viewOff:propTypes

      //@@viewOn:getDefaultProps
      //@@viewOff:getDefaultProps

      //@@viewOn:standardComponentLifeCycle
      //@@viewOff:standardComponentLifeCycle

      //@@viewOn:interface
      //@@viewOff:interface

      //@@viewOn:overridingMethods
      //@@viewOff:overridingMethods

      //@@viewOn:componentSpecificHelpers
      //@@viewOff:componentSpecificHelpers

      //@@viewOn:render
      render: function () {
        return this.getNestingLevel() ? React.createElement(tag.toLowerCase(), this.getMainAttrs(), this.getChildren() || this.getDefault().content, this.getDisabledCover()) : null;
      }
      //@@viewOff:render
    });
  },
  createLink: function (uu5Name, href, defaultContent, target) {
    return createReactClass({

      //@@viewOn:mixins
      mixins: [UU5.Common.BaseMixin, UU5.Common.ElementaryMixin, UU5.Common.ContentMixin, UU5.Common.NestingLevelMixin, UU5.Common.PureRenderMixin],
      //@@viewOff:mixins

      //@@viewOn:statics
      statics: {
        tagName: 'UU5.Bricks.Link' + uu5Name,
        nestingLevel: 'inline',
        classNames: {
          main: ns.css("link") + uu5Name.toLowerCase()
        },
        defaults: {
          content: defaultContent || uu5Name
        }
      },
      //@@viewOn:propTypes
      //@@viewOff:propTypes

      //@@viewOn:getDefaultProps
      //@@viewOff:getDefaultProps

      //@@viewOn:standardComponentLifeCycle
      //@@viewOff:standardComponentLifeCycle

      //@@viewOn:interface
      //@@viewOff:interface

      //@@viewOn:overridingMethods
      //@@viewOff:overridingMethods

      //@@viewOn:componentSpecificHelpers
      //@@viewOff:componentSpecificHelpers

      //@@viewOn:render
      render: function () {
        var mainProps = this.getMainPropsToPass();
        mainProps.href = href;
        mainProps.target = target || '_blank';

        return this.getNestingLevel() ? <Link {...mainProps}>
                {this.props.children || this.getDefault().content}
              </Link> : null;
      }
      //@@viewOn:render
    });
  }
};

