import React from 'react';
import { shallow } from 'enzyme';
import UU5 from "uu5g04";
import "uu5g04-bricks";
import enzymeToJson from 'enzyme-to-json';

const TagName = "UU5.Bricks.Button";

const This = {};

describe(`${TagName} interface testing`, () => {
  test(`${TagName} ifc setActive(true)`, () => {
    const wrapper = shallow(<UU5.Bricks.Button ref_={button => This._button = button} onClick={() => This._button.setActive(true)} />);

    wrapper.simulate('click');
    expect(This._button.state.pressed).toEqual(true);
    expect(enzymeToJson(wrapper)).toMatchSnapshot();
  });

  test(`${TagName} ifc setActive(false)`, () => {
    const wrapper = shallow(<UU5.Bricks.Button pressed ref_={button => This._button = button} onClick={() => This._button.setActive(false)} />);

    wrapper.simulate('click');
    expect(This._button.state.pressed).toEqual(false);
    expect(enzymeToJson(wrapper)).toMatchSnapshot();
  });
});